const blocks = $input.all()[0].json.results || [];

function blockToText(block) {
  const { type } = block;
  const content = block[type];

  if (!content) return '';

  switch (type) {
    case 'heading_1':
      return `# ${content.text?.[0]?.text?.content || ''}

`;
    case 'heading_2':
      return `## ${content.text?.[0]?.text?.content || ''}

`;
    case 'heading_3':
      return `### ${content.text?.[0]?.text?.content || ''}

`;
    case 'paragraph':
      return `${content.text?.[0]?.text?.content || ''}

`;
    case 'bulleted_list_item':
      return `â€¢ ${content.text?.[0]?.text?.content || ''}
`;
    case 'numbered_list_item':
      return `${content.text?.[0]?.text?.content || ''}
`;
    case 'quote':
      return `> ${content.text?.[0]?.text?.content || ''}

`;
    case 'code':
      return \`\`\`\${content.language || ''}
${content.text?.[0]?.text?.content || ''}
\`\`\`

`;
    case 'divider':
      return '---
';
    default:
      return `[${type}]
`;
  }
}

let text = '';
for (const block of blocks) {
  text += blockToText(block);
}

return [{
  json: {
    title: $input.all()[0].json.properties?.Name?.title?.[0]?.plain_text || 'Notion Document',
    content: text.trim()
  }
}];
